import React, { useState } from "react";
import '../styles/addstudent.css';
import Axios from "axios"
const AddStudents = () => {

    const [stuname, setStuName] = useState("");
    const [age, setAge] = useState(0);
    const [gender, setGender] = useState("");
    const [dep, setDep] = useState("");
    const [phone, setPhone] = useState(0);
    const [addmdate, setAddmission] = useState(null);

    const handleSubmit = (event) =>{
        event.preventDefault();
        
        // Post Request
        Axios.post('http://localhost:5000/addstudent', {
            stuname: stuname,
            age: age,
            gender: gender,
            dep: dep,
            phone: phone,
            addmdate: addmdate,
        }).then(()=>{
            console.log("DATA DONE")
        }).catch((err)=>{
            console.log(err)
        })
    }

    return (
        <span className="StuSetter">
            <header className="Stuheading">
                <h1>Add Students Information</h1>
            </header>
            <main>
                <form onSubmit={handleSubmit} className="StuForm">
                    <div>
                        <label>Name: </label>
                        <input type="text" name="stuname" placeholder="Enter Name" onChange={(event)=>{setStuName(event.target.value)}}/>
                    </div>
                    
                    <div>
                        <label>Age: </label>
                        <input type="number" name="age" onChange={(event)=>{setAge(event.target.value)}}/>
                    </div>

                    <div>
                        <label>Gender: </label>
                        <input type="radio" name="gender" value="Male" className="rad" required onChange={(event)=>{setGender(event.target.value)}}/>Male
                        <input type="radio" name="gender" value="Female" className="rad" required onChange={(event)=>{setGender(event.target.value)}}/>Female
                    </div>

                    <div>
                        <label>Department: </label>
                        <select name="dep">
                            <option value="CS" onChange={(event)=>{setDep(event.target.value)}}>CS</option>
                            <option value="SE" onChange={(event)=>{setDep(event.target.value)}}>SE</option>
                            <option value="EE" onChange={(event)=>{setDep(event.target.value)}}>EE</option>
                        </select>
                    </div>

                    <div>
                        <label>Phone No: </label>
                        <input type="number" name="phone" maxLength="11" onChange={(event)=>{setPhone(event.target.value)}}/>
                    </div>

                    <div>
                        <label>Admission Date: </label>
                        <input type="date" name="addmdate" onChange={(event)=>{setAddmission(event.target.value)}}/>
                    </div>

                    <div>
                        <input type="submit" id="subtn"/>
                    </div>
                </form>
            </main>
        </span>
    );
}
export default AddStudents;
